import React, { useState } from "react";
import { motion } from "framer-motion";

export default function App() {
  const [url, setUrl] = useState("");
  const [loading, setLoading] = useState(false);
  const [msg, setMsg] = useState("");

  const handleDownload = async (e) => {
    e.preventDefault();
    if (!url.trim()) {
      setMsg("Please enter a YouTube URL.");
      return;
    }
    setMsg("");
    setLoading(true);

    try {
      const res = await fetch(`/api/stream?url=${encodeURIComponent(url)}`);
      if (!res.ok) throw new Error(`HTTP error! status: ${res.status}`);

      const blob = await res.blob();
      const disposition = res.headers.get("content-disposition") || "";
      const match = /filename="?([^"]+)"?/.exec(disposition);
      const filename = match && match[1] ? match[1] : "video.mp4";

      const a = document.createElement("a");
      a.href = URL.createObjectURL(blob);
      a.download = filename;
      document.body.appendChild(a);
      a.click();
      a.remove();
      URL.revokeObjectURL(a.href);
      setMsg("Download started ✔️");
    } catch (err) {
      console.error(err);
      setMsg("Download failed — check the URL and backend connection.");
    } finally {
      setLoading(false);
    }
  };

  const handlePaste = async () => {
    try {
      const text = await navigator.clipboard.readText();
      if (text) {
        setUrl(text);
        setMsg("");
      }
    } catch (err) {
      console.error("Failed to read clipboard:", err);
      setMsg("Unable to access clipboard. Please paste manually.");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-4 relative overflow-hidden bg-custom-gradient">
      {/* Animated Red Accent Background Elements */}
      <div className="absolute inset-0 overflow-hidden">
        {/* Large red glow - top left */}
        <div className="absolute -top-40 -left-40 w-96 h-96 bg-red-600/30 rounded-full blur-3xl animate-pulse-slow"></div>

        {/* Medium red glow - bottom right */}
        <div className="absolute -bottom-40 -right-40 w-80 h-80 bg-red-700/20 rounded-full blur-3xl animate-pulse-slow" style={{ animationDelay: '1s' }}></div>

        {/* Small red glow - center */}
        <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-64 h-64 bg-red-500/10 rounded-full blur-3xl animate-pulse-slow" style={{ animationDelay: '2s' }}></div>

        {/* Gradient overlay */}
        <div className="absolute inset-0 bg-gradient-to-br from-black via-red-950/10 to-black"></div>

        {/* Animated red streaks */}
        <div className="absolute inset-0 bg-gradient-to-r from-transparent via-red-600/5 to-transparent animate-shimmer"></div>
      </div>

      <motion.div
        className="w-full max-w-lg relative z-10"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        {/* Header with Mirror Effect */}
        <div className="text-center mb-8 relative">
          <div className="relative inline-block">
            <h1 className="text-5xl font-bold text-white mb-2 relative z-10 font-heading tracking-tight">
              <span className="text-red-600 drop-shadow-[0_0_15px_rgba(220,38,38,0.6)]">Me</span>Tube
            </h1>
            {/* Mirror Reflection */}
            <div 
              className="absolute top-full left-0 right-0 text-5xl font-bold text-white opacity-20 font-heading"
              style={{
                transform: 'scaleY(-1)',
                background: 'linear-gradient(to bottom, rgba(220,38,38,0.4) 0%, transparent 100%)',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                filter: 'blur(1px)',
                transformOrigin: 'top',
              }}
            >
              <span className="text-red-600">Me</span>Tube
            </div>
          </div>
          <p className="text-gray-300 text-sm mt-4 font-body">Download YouTube videos in high quality</p>
        </div>

        {/* Main Card with Mirror Glass Effect */}
        <div className="mirror-card bg-gradient-to-br from-gray-900/95 via-gray-800/90 to-black/95 backdrop-blur-xl border border-red-900/30 rounded-2xl p-6 shadow-2xl relative overflow-hidden">
          {/* Red accent glow on card */}
          <div className="absolute -top-20 -right-20 w-40 h-40 bg-red-600/20 rounded-full blur-2xl"></div>

          {/* Shimmer effect */}
          <div className="absolute inset-0 -top-1/2 -left-1/2 w-1/2 h-1/2 bg-gradient-to-br from-transparent via-red-500/10 to-transparent rounded-full blur-3xl animate-shimmer pointer-events-none"></div>

          {/* Reflective surface overlay */}
          <div className="absolute inset-0 bg-gradient-to-b from-red-500/5 via-transparent to-transparent opacity-50 pointer-events-none"></div>

          {/* Red accent border glow */}
          <div className="absolute inset-0 rounded-2xl border border-red-600/20 shadow-[0_0_30px_rgba(220,38,38,0.2)] pointer-events-none"></div>

          <div className="relative z-10">
            {/* Input with Paste Button */}
            <div className="relative mb-4">
              <input
                type="text"
                value={url}
                onChange={(e) => setUrl(e.target.value)}
                placeholder="Paste YouTube URL here..."
                disabled={loading}
                className="mirror-input w-full bg-black/50 backdrop-blur-sm border border-red-900/40 rounded-lg px-4 py-3 pr-12 text-white placeholder-gray-400 focus:border-red-500 focus:ring-2 focus:ring-red-500/40 outline-none transition-all text-sm shadow-inner font-body"
              />
              <button
                type="button"
                onClick={handlePaste}
                disabled={loading}
                className="absolute right-2 top-1/2 -translate-y-1/2 px-3 py-1.5 bg-gradient-to-r from-red-600 to-red-700 hover:from-red-500 hover:to-red-600 text-white text-xs font-semibold rounded-md transition-all disabled:opacity-50 disabled:cursor-not-allowed shadow-lg shadow-red-600/40 font-body"
              >
                Paste
              </button>
            </div>

            {/* Download Button with Mirror Effect */}
            <button
              type="button"
              onClick={handleDownload}
              disabled={loading || !url.trim()}
              className="mirror-button w-full bg-gradient-to-r from-red-600 via-red-600 to-red-700 hover:from-red-500 hover:via-red-500 hover:to-red-600 text-white font-semibold py-3.5 rounded-lg transition-all disabled:bg-gray-700 disabled:cursor-not-allowed disabled:opacity-50 mb-4 relative overflow-hidden shadow-lg shadow-red-600/50 font-body"
            >
              <span className="relative z-10">
                {loading ? (
                  <span className="flex items-center justify-center gap-2">
                    <svg className="w-4 h-4 animate-spin" viewBox="0 0 24 24" fill="none">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    Downloading...
                  </span>
                ) : (
                  "Download"
                )}
              </span>
              {/* Mirror shine effect */}
              <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/30 to-transparent translate-x-[-100%] group-hover:translate-x-[100%] transition-transform duration-1000"></div>
            </button>

            {/* Message */}
            {msg && (
              <motion.div
                initial={{ opacity: 0, y: -10 }}
                animate={{ opacity: 1, y: 0 }}
                className={`text-xs text-center py-2.5 px-3 rounded-md backdrop-blur-sm font-body ${
                  msg.includes("✔️")
                    ? "bg-green-900/40 text-green-400 border border-green-700/50"
                    : msg.includes("failed")
                    ? "bg-red-900/40 text-red-400 border border-red-700/50"
                    : "bg-blue-900/40 text-blue-400 border border-blue-700/50"
                }`}
              >
                {msg}
              </motion.div>
            )}
          </div>
        </div>

        {/* Footer with subtle reflection */}
        <div className="text-center mt-6 text-gray-400 text-xs relative font-body">
          <span className="relative z-10">Made by <span className="text-red-600 font-semibold drop-shadow-[0_0_10px_rgba(220,38,38,0.5)]">antiz</span></span>
        </div>
      </motion.div>
    </div>
  );
}
